mapper.delete:

delete from goal_complete
where user_id = #{userId,jdbcType=VARCHAR}
  and group_id = #{groupId,jdbcType=INTEGER}

mapper.update:

update goal_complete
set `completion` = #{completion,jdbcType=DOUBLE},
  is_clocked = #{isClocked,jdbcType=INTEGER}
where user_id = #{userId,jdbcType=VARCHAR}
  and group_id = #{groupId,jdbcType=INTEGER}

mapper.select:

select user_id, group_id, `completion`, is_clocked
from goal_complete
where user_id = #{userId,jdbcType=VARCHAR}
  and group_id = #{groupId,jdbcType=INTEGER}

mapper.update:
update `goal_complete`
set completion = #{completion,jdbcType=DOUBLE},
is_clocked = #{isClocked,jdbcType=INTEGER}
where group_id = #{groupId,jdbcType=INTEGER} AND user_id = #{userId,jdbcType=VARCHAR}
